import type { InferrableAny } from './common';
export declare type Falsy = (false | null | undefined | 0);
export declare type Truthy<T = InferrableAny> = Exclude<T, Falsy>;
export declare type Condition<T> = Truthy<T> | Falsy;
export declare function isTruthy<T>(condition: Condition<T>): condition is Truthy<T>;
export declare function isFalsy<T>(condition: Condition<T>): condition is Falsy;
